import { clientTypes } from './client.types';

const initialState = {
  data: {
    auth: false,
    name: '',
    email: '',
    profile: '',
    birthDate: '',
    createdDate: '',
    updatedDate: '',
    token: '',
  },
}

export default function clients(state = initialState, action){
  switch(action.type){
    case clientTypes.CLIENT_GET:
      return {
        ...state,
        loading: true,
        data: {
          auth: true,
          ...action.payload,
        }
      }
    case clientTypes.CLIENT_GET_ALL:
    return {
      ...state,
      loading: true,
      data: {
        auth: true,
        ...action.payload,
      }
    }
    case clientTypes.CLIENT_UPDATE:
    return {
      ...state,
      data: {
        auth: true,
        loading: true,
        ...action.payload,
      }
    }
    case clientTypes.CLIENT_CREATE:
    return {
      ...state,
      data: {
        auth: true,
        loading: true,
        ...action.payload,
      }
    }
    default:
      return state;
  }
}
