import React from "react";
import { OutlinedInput, Button, FormControl, Card, InputLabel } from '@material-ui/core';

export default class CrudCliente extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);

      this.state = {
          newUser: {
            name: '',
            email: '',
            age: '',
            gender: '',
            expertise: '',
            about: '',

          },
      }
    }

    handleChange = ({ target }) => {
      this.setState({ [target.name]: target.value });
   };

    handleSubmit(event) {
      alert('Um nome foi enviado: ' + this.state.value);
      event.preventDefault();
    }

    render() {
      return (
        <Card>
          <form onSubmit={this.handleSubmit}>

            <FormControl variant="outlined">
              <InputLabel> Nome: </InputLabel>
              <OutlinedInput type="text" value={this.state.name} onChange={this.handleChange} />
            </FormControl>

            <Button type="submit"> Salvar </Button>

          </form>
        </Card>
      );
    }
  }
