import React from 'react';

import { OutlinedInput, FormControl, InputLabel } from '@material-ui/core';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';

const FormInput = ({ handleChange, label, ...props }) => (
    <FormControl variant="outlined">
        {label ? (
            <InputLabel htmlFor={label}>
                {label}
            </InputLabel>
        ) : null}

        <OutlinedInput id={label} onChange={handleChange} {...props} labelWidth={label.length > 5 ? label.length * 8 : 50} />
    </FormControl>
);

export const FormDate = ({ handleChange, label, ...props }) => (
    <MuiPickersUtilsProvider utils={DateFnsUtils}>
        <KeyboardDatePicker
            margin="normal"
            id={label}
            label={label}
            format="dd/MM/yyyy"
            inputVariant="outlined"
            KeyboardButtonProps={{
                'aria-label': {label},
            }}
        />
    </MuiPickersUtilsProvider>
);


export default FormInput;
