import React from "react";
import { Button, Card } from '@material-ui/core';

import FormInput from '../../../core/form-input/form-input.component';

export default class CrudCliente extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      dataNascimento: '',
      gender: '',
    }
  }


  handleSubmit = async event => {
    alert('Um nome foi enviado: ' + this.state.value);
    event.preventDefault();
  }

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  render() {
    const { name, email, dataNascimento, gender } = this.state;

    return (
      <Card>
        <form className='clienteForm' onSubmit={this.handleSubmit}>

          <FormInput
            type='text'
            name='name'
            value={name}
            onChange={this.handleChange}
            label='Nome'
            required
          />

          <FormInput
            type='email'
            name='email'
            value={email}
            onChange={this.handleChange}
            label='E-mail'
          />

          <FormInput
            type='date'
            name='dataNascimento'
            value={dataNascimento}
            onChange={this.handleChange}
            label='Data de Nascimento'
          />

          <FormInput
            type='text'
            name='gender'
            value={gender}
            onChange={this.handleChange}
            label='GÃªnero'
            required
          />

          <Button type="submit"> Salvar </Button>

        </form>
      </Card>
    );
  }
}
