import React from "react";
import CssBaseline from '@material-ui/core/CssBaseline'
import EnhancedTable from '../../components/table/EnchancedTable'
import { Input, Button } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

const data = [
  { firstName: "jane1", age: 20 },
  { firstName: "jane2", age: 20 },
  { firstName: "jane3", age: 20 },
  { firstName: "jane4", age: 20 },
  { firstName: "jane5", age: 20 },
  { firstName: "jane6", age: 20 },
  { firstName: "jane7", age: 20 },
  { firstName: "jane8", age: 20 },
  { firstName: "jane9", age: 20 },
  { firstName: "jane0", age: 20 },
  { firstName: "jane11", age: 20 },
  { firstName: "jane12", age: 20 },
  { firstName: "jane13", age: 20 },
  { firstName: "jane14", age: 20 },
  { firstName: "jane15", age: 20 },
  { firstName: "jane16", age: 20 },
  { firstName: "jane17", age: 20 },
  { firstName: "jane18", age: 20 },
  { firstName: "jane19", age: 20 },
  { firstName: "jane20", age: 20 },
  { firstName: "jane21", age: 20 },
  { firstName: "jane22", age: 20 },
  { firstName: "jane23", age: 20 },
  { firstName: "jane24", age: 20 },
  { firstName: "jane25", age: 20 },
  { firstName: "jane26", age: 20 },
  { firstName: "jane27", age: 20 },
  { firstName: "jane28", age: 20 },
  { firstName: "jane29", age: 20 },
  { firstName: "jane30", age: 20 },
  { firstName: "jane31", age: 20 },
];

const columns = [
  {
    Header: "Name",
    accessor: "firstName"
  },
  {
    Header: "Other Info",
    accessor: "age"
  }
];

const initialState = {
  connected : false,
  nextConnectionTry: 0,
  user: {Name:'', Description:'', ID_User:0},
  list: []
};




class Clientes extends React.Component {
  constructor(props) {
    super(props);

    this.state = {...initialState}
  }


  setData = () => React.useState([]);

  skipPageReset = () => React.useState(false);

  setSkipPageReset = () => React.useState(false);

  updateField = (event) => {
    const user = {...this.state.user}
    user[event.target.name] = event.target.value
    this.setState({user})
  };  

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    setSkipPageReset(true)
    setData(old =>
      old.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...old[rowIndex],
            [columnId]: value,
          }
        }
        return row
      })
    )
  }

  render() {
    return (
      <div className="Clientes">
        <div className="form">
          <div className="row">
            <div className="col-12 col-md-3">
              <div className="form-group">
                <label>Nome</label>
                <Input type="text" className="form-control" id='nameForm'
                  name="Name" value={this.state.user.Name}
                  onChange={e => this.updateField(e)}
                  placeholder="Digite o nome aqui..." />
              </div>
            </div>

            <div className="col-12 col-md-6">
              <div className="form-group">
                <label>E-mail</label>
                <Input type="text" className="form-control" id='emailForm'
                  name="Description" value={this.state.user.Description}
                  onChange={e => this.updateField(e)}
                  placeholder="Digite othis. e-mail aqui..." />
              </div>
            </div>
          </div>
          <hr />
          <div className="row">
            <div className="col-12 d-flex justify-content-end">
              <Button className="btn btn-secundarythis." onClick={e => this.clear(e)}>
                Cancelar
                </Button>
              <Button className="btn btn-primary  ml-2" onClick={e => this.save(e)}>
                Salvar
                </Button>
            </div>
          </div>
        </div>

        <CssBaseline />
        <EnhancedTable
          columns={columns}
          data={data}
          setData={this.setData}
          updateMyData={this.updateMyData}
          skipPageReset={this.skipPageReset}
        />
      </div>
    );
  }
}

export default Clientes;
