import React from "react";
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { Button, Card, CardContent, CardHeader } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';

import { create } from '../../../redux/cheque/cheque.actions';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate from '../../../core/components/form-input/form-date.component';
import './cheque-crud.styles.scss';

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  fill: {
    flexBasis: '100%',
  },
});

class CrudCheque extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      agencia: '',
      conta: '',
      numero: '',
      u3: '',
      situacao: '',
      emissao: new Date(),
      prevDeposito: new Date(),
      deposito: new Date(),
      envio: new Date(),
      devolucao: new Date(),
      pagamento: new Date(),
      valor: '',
      emitente: '',
      emitenteTipo: '',
      documento: '',
      phone: '',
    }
  }

  componentDidMount() {
    // this.getClient();
  }

  // getClient() {
  //     this.getClient = api.findById('api')
  //         .then(response => response.data)
  //         .then(data => {

  //             this.setState( ...data);
  //         });
  // }


  handleSubmit = async event => {
    event.preventDefault();

    const { agencia,
      conta,
      numero,
      u3,
      situacao,
      emissao,
      prevDeposito,
      deposito,
      envio,
      devolucao,
      pagamento,
      valor,
      emitente,
      emitenteTipo,
      documento,
      phone, } = event.target;

    const formData = {
      agencia: agencia.value,
      conta: conta.value,
      numero: numero.value,
      u3: u3.value,
      situacao: situacao.value,
      emissao: emissao.value,
      prevDeposito: prevDeposito.value,
      deposito: deposito.value,
      envio: envio.value,
      devolucao: devolucao.value,
      pagamento: pagamento.value,
      valor: valor.value,
      emitente: emitente.value,
      emitenteTipo: emitenteTipo.value,
      documento: documento.value,
      phone: phone.value,
    }


    const { dispatch } = this.props;

    dispatch(create(formData));
  }

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  handleChangeDate = (date) => {
    this.setState({ birthDate: date });
  };

  render() {
    const { agencia,
      conta,
      numero,
      u3,
      situacao,
      emissao,
      prevDeposito,
      deposito,
      envio,
      devolucao,
      pagamento,
      valor,
      emitente,
      emitenteTipo,
      documento,
      phone, } = this.state;

    return (
      <Card variant="outlined">
        <CardHeader title="Criar um Cheque" />

        <CardContent>
          <form className='chequeForm' onSubmit={this.handleSubmit}>


            <Button variant="contained" type="submit" color="primary">
              Salvar
            </Button>

          </form>
        </CardContent>
      </Card>
    );
  }
}

const mapStateToProps = (state) => {
  const { loggingIn } = state.authentication;
  return {
    loggingIn
  };
}

const connectedLoginPage = withRouter(connect(mapStateToProps, '', '', {
  pure: false
})(withStyles(styles)(CrudCheque)));

export { connectedLoginPage as CrudCheque };
