import React from "react";
import CssBaseline from '@material-ui/core/CssBaseline'
import EnhancedTable from '../../components/table/EnchancedTable'
import { Input, Button } from '@material-ui/core';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { find } from '../../redux/client/client.actions';

const columns = [
  {
    Header: "Name",
    accessor: "firstName"
  },
  {
    Header: "Other Info",
    accessor: "age"
  }
];

const initialState = {
  connected : false,
  nextConnectionTry: 0,
  user: {Name:'', Description:'', ID_User:0},
  list: []
};


class Clientes extends React.Component {
  constructor({props}) {
    super(props);

    this.state = {...initialState}
  }

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(find());
  }


  setData = () => React.useState([]);

  skipPageReset = () => React.useState(false);

  setSkipPageReset = () => React.useState(false);

  updateField = (event) => {
    const user = {...this.state.user}
    user[event.target.name] = event.target.value
    this.setState({user})
  };  

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setSkipPageReset(true)
    this.setData(old =>
      old.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...old[rowIndex],
            [columnId]: value,
          }
        }
        return row
      })
    )
  }

  render() {
    const { data } = this.props.data;
    return (
      <div className="Clientes">
        <div className="form">
          <div className="row">
            <div className="col-12 col-md-3">
              <div className="form-group">
                <label>Nome</label>
                <Input type="text" className="form-control" id='nameForm'
                  name="Name" value={this.state.user.Name}
                  onChange={e => this.updateField(e)}
                  placeholder="Digite o nome aqui..." />
              </div>
            </div>

            <div className="col-12 col-md-6">
              <div className="form-group">
                <label>E-mail</label>
                <Input type="text" className="form-control" id='emailForm'
                  name="Description" value={this.state.user.Description}
                  onChange={e => this.updateField(e)}
                  placeholder="Digite othis. e-mail aqui..." />
              </div>
            </div>
          </div>
          <hr />
          <div className="row">
            <div className="col-12 d-flex justify-content-end">
              <Button className="btn btn-secundarythis." 
                onClick={() =>['/crud']}>
                Criar
                </Button>
              <Button className="btn btn-primary  ml-2"
                onClick={() =>  this.props.history.push('/clientes/crud')}>
                Voltar
              </Button>
            </div>
          </div>
        </div>

        <CssBaseline />
        <EnhancedTable
          columns={columns}
          data={data}
          setData={this.setData}
          updateMyData={this.updateMyData}
          skipPageReset={this.skipPageReset}
        />
      </div>
    );
  }
}


const mapStateToProps = (state) =>{
 return {
    data : state.data
 };
}
const connectedClientsPage = withRouter(connect(mapStateToProps, null, null, {
 pure: false
})(Clientes));

export { connectedClientsPage as Clientes };
