import React, { useState } from "react";

import { Button, Card, CardContent, CardHeader } from '@material-ui/core';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate from '../../../core/components/form-input/form-date.component';
import './filters.styles.scss';

export default function Filters({ filters }) {

  const handleChange = e => {
    const { name, value } = e.target
    setValues({ ...filters, [name]: value })
  }

  const handleChangeDate = value => {
    setDate({ birthDate: value })
  }

  const handleSubmit = async event => {
    event.preventDefault();

    const { dispatch } = this.props;

    dispatch();
  }
  const filtersValues = filters.map(filter => ({[filter.name]: null}));

  const [values, setValues] = useState(filtersValues);
  const [dates, setDate] = useState({ birthDate: null });

  console.log("filtersValues:", filtersValues, " filters:", filters)

  return (
    <Card variant="outlined">
      <CardHeader title="Criar um Cliente" />

      <CardContent>
        <form className='clienteForm' onSubmit={handleSubmit}>

          <FormInput
            type='text'
            name='name'
            value={filters.name}
            onChange={handleChange}
            label='Nome'
            required
          />

          {filters.map(filter => {
            switch (filter.type) {
              case 'date':
                return <FormDate
                  name={filter.name}
                  value={dates.birthDate}
                  onChange={handleChangeDate}
                  label={filter.label} />
              case 'select':
                return <FormInput
                  type={filter.type}
                  name={filter.name}
                  value={values.name}
                  onChange={handleChange}
                  label={filter.label}
                  required />
              default:
                return <FormInput
                  type={filter.type}
                  name={filter.name}
                  value={values.name}
                  onChange={handleChange}
                  label={filter.label}
                  required />
            }
          })
          }

          <Button variant="contained" type="submit" color="primary">
            Salvar
            </Button>

        </form>
      </CardContent>
    </Card>
  );
}
