import React, { useState } from "react";

import { Button, Card, CardContent, CardHeader } from '@material-ui/core';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate  from '../../../core/components/form-input/form-date.component';
import './filters.styles.scss';

export default function Filters() {

    const handleChange = e => {
        const {name, value} = e.target
        setValues({...filters, [name]: value})
    }

    const handleChangeDate = value => {
        setDate({birthDate: value})
    }

    const handleSubmit = async event => {
        event.preventDefault();

        const { dispatch } = this.props;

        dispatch();
      }

    const [filters, setValues] = useState({name: ''})
    const [date, setDate] = useState({birthDate: null})

    return (
      <Card variant="outlined">
        <CardHeader title="Criar um Cliente"/>

        <CardContent>
          <form className='clienteForm' onSubmit={handleSubmit}>

            <FormInput
              type='text'
              name='name'
              value={filters.name}
              onChange={handleChange}
              label='Nome'
              required
            />

            <Button variant="contained" type="submit" color="primary">
              Salvar
            </Button>

          </form>
        </CardContent>
      </Card>
    );
}
