import React, { useState } from "react";

import { Button, Card, CardContent, CardHeader, ButtonGroup, Select, Link, LinkButton } from '@material-ui/core';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate from '../../../core/components/form-input/form-date.component';
import FormSelect from '../../../core/components/form-input/form-select.component';
import './filters.styles.scss';

export default function Filters({ filters, handleSubmit, history, match }) {

  const handleSubmitForm = async event => {
    event.preventDefault();

    handleSubmit(values);
  }

  const handleChange = e => {
    const { name, value } = e.target
    // setValues({ ...filters, [name]: value })

    setValues(prevFilters => (prevFilters.map((filter) => {
      if (filter.name === name) return { ...filter, value: value }
      return filter;
    })))
  }


  const [values, setValues] = useState(filters);
  const preventDefault = (event) => event.preventDefault();

  return (
    <Card variant="outlined">
      <CardHeader title="Criar um Cliente" />

      <CardContent>
        <form className='clienteForm' onSubmit={handleSubmitForm}>

          {filters.map((filter, index) => {
            switch (filter.type) {
              case 'date':
                return <FormDate
                  key={filter.name}
                  name={filter.name}
                  value={values[filter.value]}
                  onChange={date => handleChange({ target: { name: filter.name, value: date } })}
                  label={filter.label} />

              case 'select':
                return <FormSelect
                  key={filter.name}
                  name={filter.name}
                  value={values[filter.value]}
                  onChange={handleChange}
                  label={filter.label}
                  selects={filter.selects}
                />

              default:
                return <FormInput
                  key={filter.name}
                  type={filter.type}
                  name={filter.name}
                  value={values[filter.value]}
                  onChange={handleChange}
                  label={filter.label}
                />
            }
          })
          }

          <ButtonGroup className="btn-group">
            <Button variant="contained" type="submit" color="primary">
              Buscar
            </Button>

            <Button variant="contained" onClick={() => history.push(`${match.url}/crud`)} type="button" color="secondary">
              Cadastrar
            </Button>

            <Button variant="contained" type="button">
              Voltar
            </Button>

            <LinkButton
                  to='/clients/crud'
                  onClick={(event) => {
                    console.log('custom event here!', event)
                  }}
                >Push My Buttons!</LinkButton>
          </ButtonGroup>

        </form>
      </CardContent>
    </Card>
  );
}
