import React from "react";
import { Button, Card, CardContent, CardHeader } from '@material-ui/core';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate  from '../../../core/components/form-input/form-date.component';
import './cliente-crud.styles.scss';

export default class CrudCliente extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      dataNascimento: new Date('2014-08-18T21:11:54'),
      gender: '',
    }
  }


  handleSubmit = async event => {
    alert('Um nome foi enviado: ' + this.state.value);
    event.preventDefault();
  }

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  handleDateChange = (date) => {
    this.setState({ dataNascimento: date });
  };

  render() {
    const { name, email, dataNascimento, gender } = this.state;

    return (
      <Card variant="outlined">
        <CardHeader title="Criar um Cliente"/>

        <CardContent>
          <form className='clienteForm' onSubmit={this.handleSubmit}>

            <FormInput
              type='text'
              name='name'
              value={name}
              onChange={this.handleChange}
              label='Nome'
              required
            />

            <FormInput
              type='email'
              name='email'
              value={email}
              onChange={this.handleChange}
              label='E-mail'
            />

            <FormDate />

            <FormInput
              type='text'
              name='gender'
              value={gender}
              onChange={this.handleChange}
              label='GÃªnero'
              required
            />

            <Button type="submit"> Salvar </Button>

          </form>
        </CardContent>
      </Card>
    );
  }
}
