import React from "react";
import { Button, Card, CardContent, CardHeader } from '@material-ui/core';

import api from '../../../core/services/api';
import { create } from '../../../redux/client/client.actions';

import FormInput from '../../../core/components/form-input/form-input.component';
import FormDate  from '../../../core/components/form-input/form-date.component';
import './cliente-crud.styles.scss';

export default class CrudCliente extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      birthDate: new Date(),
      gender: '',
      cpf: '',
      phone: '',
      address: ''
    }
  }

  componentDidMount() {
    this.getClient();
  }

  getClient() {
      this.getClient = api.findById('api')
          .then(response => response.data)
          .then(data => {

              this.setState( ...data);
          });
  }


  handleSubmit = async event => {
    const { name, email, birthDate, gender, cpf, phone, address } = event.target;
    const { user } = this.props;
    const formData = {
      name: name.value,
      email: email.value,
      birthDate: birthDate.value,
      gender: gender.value,
      cpf: cpf.value,
      phone: phone.value,
      address: address.value,
    }

    create(formData, user.token);

    event.preventDefault();
  }

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  handleChangeDate = (date) => {
    this.setState({ birthDate: date });
  };

  render() {
    const { name, email, birthDate, gender, phone, address, cpf } = this.state;

    return (
      <Card variant="outlined">
        <CardHeader title="Criar um Cliente"/>

        <CardContent>
          <form className='clienteForm' onSubmit={this.handleSubmit}>

            <FormInput
              type='text'
              name='name'
              value={name}
              onChange={this.handleChange}
              label='Nome'
              required
            />

            <FormInput
              type='email'
              name='email'
              value={email}
              onChange={this.handleChange}
              label='E-mail'
            />

            <FormDate
              name='birthDate'
              value={birthDate}
              onChange={this.handleChangeDate}
              label='Data de Nascimento' />

            <FormInput
              type='text'
              name='gender'
              value={gender}
              onChange={this.handleChange}
              label='Gênero'
              required
            />

            <FormInput
              type='text'
              name='cpf'
              value={cpf}
              onChange={this.handleChange}
              label='CPF'
              required
            />

            <FormInput
              type='text'
              name='phone'
              value={phone}
              onChange={this.handleChange}
              label='Telefone'
              required
            />

            <FormInput
              type='text'
              name='address'
              value={address}
              onChange={this.handleChange}
              label='Endereço'
              required
            />

            <Button variant="contained" type="submit" color="primary">
              Salvar
            </Button>

          </form>
        </CardContent>
      </Card>
    );
  }
}
