import React from "react";
import { Button, Card } from '@material-ui/core';

import FormInput from '../../../core/form-input/form-input.component';

export default class CrudCliente extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      age: '',
      gender: '',
      expertise: '',
      about: '',
      Button
    }
  }


  handleSubmit(event) {
    alert('Um nome foi enviado: ' + this.state.value);
    event.preventDefault();
  }

  handleChange = ({ target }) => {
    this.setState({ [target.name]: target.value });
  };

  render() {
    return (
      <Card>
        <form onSubmit={this.handleSubmit}>

          <FormInput
            type='text'
            name='name'
            value={name}
            onChange={this.handleChange}
            label='Nome'
            required
          />

          <Button type="submit"> Salvar </Button>

        </form>
      </Card>
    );
  }
}
